mapper.select:
SELECT
    DISTINCT u.*
FROM users u

LEFT JOIN proposals p ON u.id = p.user
LEFT JOIN cospeakers c ON c.proposal_id = p.id AND c.user_id = u.id



        u.email = #{email}


        AND p.state IN

            #{item}

        AND p.event_id = #{eventId}



    ORDER BY

        ${sortItem} ${order}
